import React, { useState, useEffect } from "react";
import axios from "axios";
import { API_URL } from "../config";
import "./chracter.css";

function Character() {
  let [page, setPage] = useState(1);
  const [character, setCharacter] = useState(null);
  let [serachItem, setSeachItem] = useState(null);

  useEffect(() => {
    try {
      async function getcharacter() {
        let response = await axios.get(`${API_URL}/character/?page=${page}`);
        if (serachItem) {
          const searchCharacter = serachItem.every(([key, value]) => {
            if (serachItem[key] === "species") {
              console.log(serachItem[value]);
            }
          });
        }

        setCharacter(response.data.results);
      }
      getcharacter();
    } catch (err) {}
  }, [page, serachItem]);

  function changePage() {
    setPage((page += 1));
  }

  const matches = (item) => {
    const inputs = serachItem;
    const ItemEntries = Object.entries(item);

    const matchedItem = ItemEntries.every(([key, value]) => {
      const inputIsEmpty = !inputs[key];
      if (inputIsEmpty) return true;
      else {
        const valueLowercased = value.toString().toLowerCase();
        const inputLowercased = inputs[key].trim().toLowerCase();
        return valueLowercased.includes(inputLowercased);
      }
    });

    return matchedItem;
  };

  const handleSearch = (event) => {
    const { value, name } = event.target;
    setSeachItem({ [name]: value });
  };

  console.log();

  return (
    <>
      <h1>Character</h1>
      Species
      <input
        placeholder="Filter Species"
        name="species"
        onChange={(e) => handleSearch(e, "species")}
      />
      Status
      <input
        placeholder="Filter Status"
        name="status"
        onChange={(e) => handleSearch(e, "status")}
      />
      <grid>
        {character &&
          character.map((element) => (
            <>
              <div className="item">
                <a href={element.url}>
                  <img src={element.image} alt="img" />
                  <br />
                  <span>{element.name}</span>
                  <span>{element.species}</span>
                  <br />
                  <span>{element.status}</span>
                  <span>{element.created}</span>
                </a>
              </div>
            </>
          ))}
        {/* {pageInfo && (
          <>
            <span>{pageInfo.count}</span>
            <span>{pageInfo}</span>
          </>
        )} */}
      </grid>
      <button onClick={changePage}>{page}</button>
    </>
  );
}

export default Character;
